import { useState } from 'react'
import React from 'react'


const Item = ({buy, title, description, stock}) =>{
    const [stockItem, setStockItem] = useState(stock)

    const reduceStock = () => {
        setStockItem(stockItem-1)
    }

    return (
      <>
    <div className='item'>
        <h3>{title}</h3>
        <p>{description}</p>
        <h5>En stock: <span>{stockItem <= 0 ? "agotado" : stockItem}</span></h5>
        <button disabled = {stockItem <= 0} onClick={() => {buy(); reduceStock();}}>Comprar</button>
    </div>
    </>
    )
}
export default Listado;
